mapper.select:
SELECT count(a.board_no) cnt FROM brd_lecture a, top_mbr_bsc b WHERE a.user_no = b.user_no AND 1=1
AND a.title like '%${boardSrcWord}%'
AND (a.title like '%${boardSrcWord}%' OR a.contents like '%${boardSrcWord}%')
AND b.user_name like '%${boardSrcWord}%'
AND a.pkey = #{titleSearch}
AND a.board_type = #{boardType}

mapper.insert:
INSERT INTO brd_lecture (
	user_no
	,pkey			
	,title
	,contents
	,reg_ip
	,reg_date
	,view_cnt
	,board_type
)VALUES(
	#{user_no}		
	,#{pkey}	
	,#{title}
	,#{contents}
	,#{reg_ip}
	,unix_timestamp(now())
	,0
	,#{board_type}
)

		SELECT LAST_INSERT_ID()

mapper.select:
SELECT max(board_no)
FROM brd_lecture
WHERE user_no = #{user_no}

mapper.update:
UPDATE brd_lecture SET 
	pkey = #{pkey}
WHERE board_no = #{board_no}

UPDATE brd_lecture SET 
	pkey = #{pkey}			
	,title = #{title}
	,contents = #{contents}
WHERE board_no = #{board_no}
