mapper.select:
SELECT a.board_no , a.user_no , a.category_id , getCodeValue('brd_ref_report', 'cate_code', a.category_id) category_id_nm , a.event_id , getCodeValue('brd_ref_report', 'event_code', a.event_id) event_id_nm , a.type_id , getCodeValue('brd_ref_report', 'type_code', a.type_id) type_id_nm , a.title , (select count(board_file_no) from brd_ref_report_file where board_no = a.board_no) file_cnt , getTopMbrBscUserName( a.user_no ) user_nm , a.contents, a.reg_ip , a.reg_date , DATE_FORMAT(a.reg_date, '%Y-%m-%d') reg_date_fmt , a.view_cnt , (select MAX(board_file_no) from brd_ref_report_file where board_no = a.board_no) board_file_no FROM brd_ref_report a, top_mbr_bsc b WHERE a.user_no = b.user_no AND a.category_id = #{cateSearch}
AND a.title like '%${boardSrcWord}%'
AND (a.title like '%${boardSrcWord}%' OR a.contents like '%${boardSrcWord}%')
AND b.user_name like '%${boardSrcWord}%'
AND a.event_id = #{eventSearch}
AND a.type_id = #{typeSearch}
ORDER BY a.board_no DESC LIMIT #{queryPgNoInt}, 15

SELECT COUNT(*) cnt						
FROM brd_ref_report a, top_mbr_bsc b
WHERE a.user_no = b.user_no
AND a.category_id = #{cateSearch}



			AND a.title like '%${boardSrcWord}%'




			AND (a.title like '%${boardSrcWord}%' OR a.contents like '%${boardSrcWord}%')




			AND b.user_name like '%${boardSrcWord}%'




	AND a.event_id = #{eventSearch}


	AND a.type_id = #{typeSearch}

mapper.insert:
INSERT INTO brd_ref_report(
	user_no
	,category_id
	,event_id
	,type_id
	,title
	,contents
	,reg_ip
	,reg_date
	,view_cnt
)VALUES(
	#{user_no}
	,#{category_id}
	,#{event_id}
	,#{type_id}
	,#{title}
	,#{contents}
	,#{reg_ip}
	,now()
	,0
)		

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE brd_ref_report SET 
	event_id = #{event_id}
	,type_id = #{type_id}			
	,title = #{title}
	,contents = #{contents}
WHERE board_no = #{board_no}
