mapper.select:
SELECT a.board_no, a.user_no, b.user_name user_nm , ptnr_id, getTopPtnrBscPtnrName(ptnr_id) ptnr_name , title_classify, getCodeValue('brd_notice_top', 'title_classify', a.title_classify) title_classify_nm , type_classify, getCodeValue('brd_reference_ptnr', 'type_classify', a.type_classify) type_classify_nm , year_classify, a.title, a.contents, a.reg_ip , a.reg_date
, DATE_FORMAT(FROM_UNIXTIME(a.reg_date), '%Y-%m-%d') reg_date_fmt , a.view_cnt , (select count(board_file_no) from brd_reference_ptnr_file where board_no = a.board_no) file_cnt , (SELECT MAX(board_file_no) FROM brd_reference_ptnr_file WHERE board_no = a.board_no) board_file_no , (select count(*) from brd_reference_ptnr_memo where fk_no = a.board_no) memo_cnt , FLOOR((UNIX_TIMESTAMP(NOW()) - a.reg_date) / 24 / 60 / 60) day_cnt FROM brd_reference_ptnr a, top_mbr_bsc b WHERE a.user_no = b.user_no AND a.category_id = #{cateSearch}
AND a.title like '%${boardSrcWord}%'
AND (a.title like '%${boardSrcWord}%' OR a.contents like '%${boardSrcWord}%')
AND b.user_name like '%${boardSrcWord}%'
AND a.ptnr_id in (SELECT ptnr_id FROM top_ptnr_bsc WHERE ptnr_group_order = (SELECT ptnr_group_order FROM top_ptnr_bsc WHERE ptnr_id = #{ptnrSearch}))
AND a.ptnr_id = #{ptnrSearch}
AND a.title_classify = #{titleSearch}
AND a.type_classify = #{typeSearch}
AND a.year_classify = #{yearSearch}
ORDER BY a.board_no DESC LIMIT #{queryPgNoInt}, 15

SELECT count(a.board_no) cnt
FROM brd_reference_ptnr a, top_mbr_bsc b
WHERE a.user_no = b.user_no
AND a.category_id = #{cateSearch}



			AND a.title like '%${boardSrcWord}%'




			AND (a.title like '%${boardSrcWord}%' OR a.contents like '%${boardSrcWord}%')




			AND b.user_name like '%${boardSrcWord}%'






			AND a.ptnr_id in (SELECT ptnr_id FROM top_ptnr_bsc WHERE ptnr_group_order
									= (SELECT ptnr_group_order FROM top_ptnr_bsc WHERE ptnr_id = #{ptnrSearch}))


			AND a.ptnr_id = #{ptnrSearch}




	AND a.title_classify = #{titleSearch}


	AND a.type_classify = #{typeSearch}


	AND a.year_classify = #{yearSearch}

mapper.insert:
INSERT INTO brd_reference_ptnr (
	user_no
	, category_id
	, ptnr_id
	, title_classify
	, type_classify
	, year_classify
	, title, contents
	, reg_ip
	, reg_date
	, view_cnt
	)VALUES(
	#{user_no}
	, #{category_id}
	, #{ptnr_id}
	, #{title_classify}
	, #{type_classify}
	, #{year_classify}
	, #{title}
	, #{contents}
	, #{reg_ip}
	, unix_timestamp(now())
	, 0
	)

mapper.select:
SELECT max(board_no)
FROM brd_reference_ptnr
WHERE user_no = #{user_no}

mapper.update:
UPDATE brd_reference_ptnr SET ptnr_id = #{ptnr_id}, title_classify = #{title_classify}
	, type_classify = #{type_classify}, year_classify = #{year_classify}
	, title = #{title}, contents = #{contents}
WHERE board_no = #{board_no}
