mapper.select:
SELECT a.board_no , a.category_id , a.user_no , b.user_name user_nm , a.category_id , getCodeValue('brd_ref_report', 'cate_code', a.category_id) category_id_nm , a.event_id , getCodeValue('brd_ref_report', 'event_code', a.event_id) event_id_nm , a.type_id , getCodeValue('brd_ref_report', 'type_code', a.type_id) type_id_nm , a.title , a.contents , a.reg_ip , a.reg_date
, DATE_FORMAT(FROM_UNIXTIME(a.reg_date), '%Y-%m-%d') reg_date_fmt , a.view_cnt , (select count(board_file_no) FROM brd_reference_top_file where board_no = a.board_no) file_cnt , (SELECT MAX(board_file_no) FROM brd_reference_top_file WHERE board_no = a.board_no) board_file_no , (select count(*) from brd_reference_top_memo where fk_no = a.board_no) memo_cnt FROM brd_reference_top a, top_mbr_bsc b WHERE a.user_no = b.user_no AND a.category_id = #{cateSearch}
AND a.title like '%${boardSrcWord}%'
AND (a.title like '%${boardSrcWord}%' OR a.contents like '%${boardSrcWord}%')
AND b.user_name like '%${boardSrcWord}%'
AND a.event_id = #{eventSearch}
AND a.type_id = #{typeSearch}
ORDER BY a.board_no DESC LIMIT #{queryPgNoInt}, 15

SELECT count(a.board_no) cnt
FROM brd_reference_top a, top_mbr_bsc b
WHERE a.user_no = b.user_no
AND a.category_id = #{cateSearch}



			AND a.title like '%${boardSrcWord}%'




			AND (a.title like '%${boardSrcWord}%' OR a.contents like '%${boardSrcWord}%')




			AND b.user_name like '%${boardSrcWord}%'




	AND a.event_id = #{eventSearch}


	AND a.type_id = #{typeSearch}

mapper.insert:
INSERT INTO brd_reference_top (
	user_no
	,category_id
	,event_id
	,type_id
	,title
	,contents
	,reg_ip
	,reg_date
	,view_cnt
)VALUES(
	#{user_no}
	,#{category_id}
	,#{event_id}
	,#{type_id}
	,#{title}
	,#{contents}
	,#{reg_ip}
	,unix_timestamp(now())
	,0
)

		SELECT LAST_INSERT_ID()

mapper.select:
SELECT max(board_no)
FROM brd_reference_top
WHERE user_no = #{user_no}

mapper.update:
UPDATE brd_reference_top SET 
	category_id = #{category_id}
WHERE board_no = #{board_no}

UPDATE brd_reference_top SET 
	event_id = #{event_id}
	,type_id = #{type_id}			
	,title = #{title}
	,contents = #{contents}
WHERE board_no = #{board_no}
