mapper.select:
SELECT column_name FROM Information_schema.columns
WHERE table_schema = 'toplac'
AND table_name = #{selectUserTable}

SELECT tbl_nm, col_nm, col_cd col_cd1, col_val
FROM sysadm_codedic
WHERE tbl_nm = #{selectUserTable}

	AND col_nm = #{selectColumn}

ORDER BY tbl_nm, col_nm, col_cd

SELECT tbl_nm, col_nm, col_cd1, col_cd2, col_val
FROM sysadm_codedic2
WHERE tbl_nm = #{selectUserTable}

	AND col_nm = #{selectColumn}

ORDER BY tbl_nm, col_nm, col_cd1, col_cd2
